    private void btComprobarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btComprobarActionPerformed
        // TODO add your handling code here:
        
        String cadena = txtPalabra.getText();
        //llamar al metodo
        this.contarEspacios(cadena);
        //igualar la variable cantidadEspacios al metodo
        int cantidadEspacios = contarEspacios(cadena);
        //Mostrar la cantidad de espacios
        lblEspacios.setText("Espacios: "+cantidadEspacios);
        
    }//GEN-LAST:event_btComprobarActionPerformed

    public static int contarEspacios(String cadena) {
        int contador = 0;
        // Iterar sobre cada caracter de la cadena
        for (char caracter : cadena.toCharArray()) {
            // Verificar si el car√°cter es un espacio en blanco
            if (Character.isWhitespace(caracter)) {
                contador++;
            }
        }
        return contador;
    }
